import './App.css';
import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { v4 as uuidv4 } from 'uuid';

function App() {
  const [file, setFile] = useState(null);
  const [userInput, setUserInput] = useState('');
  const [messages, setMessages] = useState([]);
  const [sessionId, setSessionId] = useState('');

  useEffect(() => {
    // Generate a unique session ID when the component mounts
    setSessionId(uuidv4());
  }, []);

  function handleFileChange(event) {
    setFile(event.target.files[0]);
  }

  function handleUpload(event) {
    event.preventDefault();
    if (!file) {
      alert('Please select a file to upload.');
      return;
    }

    const url = 'http://localhost:8000/upload-menu';
    const formData = new FormData();
    formData.append('file', file);
    formData.append('session_id', sessionId);

    const config = {
      headers: {
        'content-type': 'multipart/form-data',
      },
    };

    axios.post(url, formData, config)
      .then((response) => {
        console.log(response.data);
        setMessages((prevMessages) => [...prevMessages, { type: 'bot', text: response.data }]);
      })
      .catch((error) => {
        console.error('Error uploading file:', error);
      });
  }

  function handleChatSubmit(event) {
    event.preventDefault();
    if (!userInput.trim()) {
      alert('Please enter a message.');
      return;
    }

    const url = 'http://localhost:8000/chat';
    const data = {
      human_input: userInput,
      session_id: sessionId,
    };

    axios.post(url, data)
      .then((response) => {
        console.log(response.data);
        setMessages((prevMessages) => [
          ...prevMessages,
          { type: 'user', text: userInput },
          { type: 'bot', text: response.data },
        ]);
        setUserInput('');
      })
      .catch((error) => {
        console.error('Error chatting with bot:', error);
      });
  }

  return (
    <div className="App">
      <h1>Menu Analyzer Chatbot</h1>
      <form onSubmit={handleUpload}>
        <input type="file" onChange={handleFileChange} />
        <button type="submit">Upload Menu</button>
      </form>
      <form onSubmit={handleChatSubmit}>
        <input
          type="text"
          value={userInput}
          onChange={(e) => setUserInput(e.target.value)}
          placeholder="Ask something..."
        />
        <button type="submit">Send</button>
      </form>
      <div>
        <h2>Messages</h2>
        <ul>
          {messages.map((msg, index) => (
            <li key={index} className={msg.type}>
              <strong>{msg.type === 'user' ? 'You' : 'Bot'}:</strong> {msg.text}
            </li>
          ))}
        </ul>
      </div>
    </div>
  );
}

export default App;
