import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { v4 as uuidv4 } from 'uuid';
import { Button } from 'react-bootstrap';
import './App.css'; // Assuming this file exists for styling
import { RemoteRunnable } from "@langchain/core/runnables/remote";

async function App() {
  const [file, setFile] = useState(null);
  const [messages, setMessages] = useState([]);
  const [sessionId, setSessionId] = useState('');
  const chat = RemoteRunnable("http://localhost:8000/chat")
  useEffect(() => {
    // Generate a unique session ID when the component mounts
    setSessionId(uuidv4());
  }, []);

  // Function to handle file selection
  const handleFileChange = (event) => {
    setFile(event.target.files[0]);
  };

  // Function to handle file upload

  const handleUpload = (event) => {

  };

  // Function to handle chat message submission
  const handleChat = (event) => {
    chat.run({human_input: event.target.message.value})

  };

  return (
    <div className="App">
      <h1>Menu Analyzer Chatbot</h1>
      <form onSubmit={handleUpload}>
        <input type="file" onChange={handleFileChange} />
        <Button variant="success" type="submit">Upload</Button>
        <input type="hidden" name="session_id" value={sessionId} />
      </form>

      <form onSubmit={handleChat}>
        <input type="text" name="message" />
        <Button variant="primary" type="submit">Send</Button>
      </form>

    </div>
  );
}

export default App;
