import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { v4 as uuidv4 } from 'uuid';
import { Button } from 'react-bootstrap';
import './App.css'; // Assuming this file exists for styling
import { RemoteRunnable } from "@langchain/core/runnables/remote";

async function App() {
  const [file, setFile] = useState(null);
  const [messages, setMessages] = useState([]);
  const [sessionId, setSessionId] = useState('');
  const chat = RemoteRunnable("http://localhost:8000/chat")
  useEffect(() => {
    // Generate a unique session ID when the component mounts
    setSessionId(uuidv4());
  }, []);

  // Function to handle file selection
  const handleFileChange = (event) => {
    setFile(event.target.files[0]);
  };

  // Function to handle file upload

  const handleUpload = (event) => {
    event.preventDefault();
    if (!file) {
      alert('Please select a file to upload.');
      return;
    }

    const url = `http://localhost:8000`;
    const formData = new FormData();
    formData.append('file', file);
    formData.append('session_id', sessionId);

    const config = {
      headers: {
        'content-type': 'multipart/form-data',
      },
    };

    axios.post(url, formData, config)
      .then((response) => {
        console.log(response.data);
        setMessages((prevMessages) => [...prevMessages, { type: 'bot', text: response.data }]);
      })
      .catch((error) => {
        console.error('Error uploading file:', error);
        alert('Error uploading file. Please try again.');
      });
  };

  // Function to handle chat message submission
  const handleChat = (event) => {
    chat.run({human_input: event.target.message.value})

  };

  return (
    <div className="App">
      <h1>Menu Analyzer Chatbot</h1>
      <form onSubmit={handleUpload}>
        <input type="file" onChange={handleFileChange} />
        <Button variant="success" type="submit">Upload</Button>
        <input type="hidden" name="session_id" value={sessionId} />
      </form>

      <form onSubmit={handleChat}>
        <input type="text" name="message" />
        <Button variant="primary" type="submit">Send</Button>
      </form>

    </div>
  );
}

export default App;
